import { useState } from "react";

const Tasks = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: "doctor appoinment",
      day: "feb 5th at 2pm",
      reminder: true,
    },
    {
      id: 2,
      text: "doctor ",
      day: "feb ",
      reminder: true,
    },
    {
      id: 3,
      text: "hi ",
      day: "jan ",
      reminder: false,
    },
  ])

  return (
    <>
      {tasks.map((task) => (
        <h3 key={task.id}>{task.text}</h3>
      ))}
    </>
  );
};

export default Tasks;
