import { useState } from "react";
import "./App.css";
import Header from "./components/Header";
import Tasks from "./components/Tasks";
import AddTask from "./components/AddTask";

// our events are stored in our App.js
const App = () => {
  const [showAddTask, setShowAddTask] = useState(false);
  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: "doctor appoinment",
      day: "feb 5th at 2pm",
      reminder: true,
    },
    {
      id: 2,
      text: "doctor ",
      day: "feb ",
      reminder: true,
    },
    {
      id: 3,
      text: "hi ",
      day: "jan ",
      reminder: false,
    },
  ]);
  // State: we use it here bcs we wanna be able to use this whitin another components
  // if we keep it in Tasks.js we could only use that in that specific componenet
  // setTask([...tasks, {}])  add a new object to tasks

  //Add Task
  const addTask = (task) => {
    // create id by myself
    const id = Math.floor(Math.random() * 10000) + 1;
    const newTask = { id, ...task };
    setTasks([...tasks, newTask]);
  };

  //Delete Task
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  //Toggle Reminder
  const toggleReminder = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, reminder: !task.reminder } : task
      )
    );
  };

  return (
    <div className="container">
      <Header onAdd={() => setShowAddTask(!showAddTask)}/>
      {/* && it means if its true then */}
      {showAddTask && <AddTask onAdd={addTask} />}
      {tasks.length > 0 ? (
        <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder} />
      ) : (
        "No Tasks To Show"
      )}
    </div>
  );
};

export default App;
